const postReducer = ({
    state = {posts: null, loading: false, error: false, uploading: false},
    action
}) => {
    switch(action.type){
        case "UPLOAD_START":
            return {...state, uploading: true, error: false}
        case "UPLOAD_SUCCESS":
            return {...state, posts: [action.data,...state.posts], 
            uploaing: false, error: false}
        case "UPLOAD_FAIL":
            return {...state, uploading: false, error: true}
        default:
            return state
    }
}
export default postReducer
